
function addInput(divName, Pagamento, valorProduto) {
    var tipoPagamento = document.getElementsByName(Pagamento);
    var valorProduto = document.getElementById(valorProduto);

    for (var i = 0, length = tipoPagamento.length; i < length; i++) {
        if (tipoPagamento[i].value === 'cartao' && tipoPagamento[i].checked) {

            var newdiv = document.createElement('div');
            newdiv.innerHTML =  "<div id='modoPagamentoCartao'> ";
            newdiv.innerHTML += " Como quer pagar essa disgraca? <br/> ";
            newdiv.innerHTML += " <input type='radio' name='pagamentoCartao' value='aVista'> A vista <br/> " ;
            newdiv.innerHTML += " <input type='radio' name= 'pagamentoCartao' value= 'aPrazo' > A prazo <br/>";
            newdiv.innerHTML += "</div> ";
            newdiv.innerHTML += " <button onClick='PagamentoCartaoAVista('modoPagamentoCartao', 'pagamentoCartao')'>A Vista </button> ";
            document.getElementById(divName).appendChild(newdiv);

            break;
        } else if (tipoPagamento[i].value === 'dinheiro' && tipoPagamento[i].checked) {

            var newdiv = document.createElement('div');
            newdiv.innerHTML = "<button onClick='PagamentoComDesconto(valorProduto.value)'>pagamento Dinheiro</button>";
            document.getElementById(divName).appendChild(newdiv);
            console.log(valorProduto.value);

            break;
        }
    }
}

function PagamentoComDesconto(valorProduto) {
    var valorDesconto = valorProduto * 0.10;
    valorProduto = valorProduto - valorDesconto;
    console.log(valorProduto);
}

function PagamentoCartaoAVista(modoPagamentoCartao, pagamentoCartao){

    console.log(modoPagamentoCartao);
    console.log(pagamentoCartao);
    
}
