function comprarAvistaComDesconto(valorProduto) {
    var valorDesconto = valorProduto.value * 0.10;
    var resultado = valorProduto.value - valorDesconto;
    alert('O Valor a pagar: R$' + parseFloat(resultado).toFixed(2))
}

function comprarAvista(valorProduto) {
    var resultado = valorProduto.value;
    alert('O Valor a pagar: R$' + parseFloat(resultado).toFixed(2))
}

function comprarAprazo(valorProduto) {
    document.getElementById("inputParcelas").innerHTML = "Parcelas: <input type='number' class='form- control' id='numeroParcelas'/>"
        + "<input type='button' onclick='comprarAprazoComParcelas(valorProduto,numeroParcelas)' value='Comprar'><br /><br/>";
}
function comprarAprazoComParcelas(valorProduto, numeroParcelas) {

    var precoTotal = parseFloat(valorProduto.value);
    var valorParcela = 0;
    var resultado = 0;

    for (let i = numeroParcelas.value; i > 0; i--) {
        valorParcela += precoTotal * 0.02
    }

    resultado = precoTotal + valorParcela;

    var resultadoParcelado = (resultado / numeroParcelas.value)

    alert('O Valor a pagar: R$' + parseFloat(resultado).toFixed(2) + '\n' +
        'O Valor a pagar Parcelado: R$' + parseFloat(resultadoParcelado).toFixed(2))

}
